/*
 * leetcode #839 similar-string-groups 相似字符串组
 * https://leetcode-cn.com/problems/similar-string-groups/description/
 */
#include <stdlib.h>
#include <string>
#include <cstring>
#include <algorithm>
#include <vector>

#include <iostream>
#include <assert.h>
#include <time.h>
using namespace std;

/*
 * 并查集
 */
class DisjointSet {

private:
    int *parent;
    int *rank;
    int len; // len = sizeof(parent) / sizeof(parent[0]);

    // #define MAX 10000
    // struct Node {
    //     int data;
    //     int rank;
    //     int parent;
    // }node[MAX];

public:
    DisjointSet(int n) {
        parent = new int[n];
        rank = new int[n];
        len = n;
        memset(parent, -1, sizeof(int) * n);
        memset(rank, 0, sizeof(int) * n);
    }

    ~DisjointSet() {
        delete[] parent;
        parent = NULL;
        delete[] rank;
        rank = NULL;
        len = 0;
    }

    int findRoot(int n) {
        return parent[n] < 0 ? n : parent[n] = findRoot(parent[n]); // path compression
    }

    int link(int n1, int n2) {
        int root1 = findRoot(n1);
        int root2 = findRoot(n2);
        if (root1 == root2) {
            return root1;
        }
        // rank merge
        if (rank[root1] < rank[root2]) {
            int tmp = root1;
            root1 = root2;
            root2 = tmp;
        } else if (rank[root1] == rank[root2]) {
            ++rank[root1];
        }
        // end rank merge
        // cout << rank[root1] << endl;
        parent[root2] = root1; // union

        return root1;
    }

    bool isEqual(int n1, int n2) {
        return findRoot(n1) == findRoot(n2);
    }

    int count() {
        int cnt = 0;
        for(int i = 0; i < len; ++i) {
            if (parent[i] < 0) {
                ++cnt;
            }
        }
        return cnt;
    }
};

class Solution {
private:
    bool isSimilar(string s1, string s2){
        // assert(s1.length() == s2.length());
        int cnt = 0;
        int l = s1.length();
        for (int i = 0; i < l; ++i) {
            if (s1[i] != s2[i]){
                ++cnt;
                if (cnt > 2) {
                    return false;
                }
            }
        }
        return true; // cnt == 2;
    }

public:
    int numSimilarGroups(vector<string>& A) {
        int l = A.size();
        DisjointSet s(l);

        for(int i = 0; i < l; ++i) {
            for(int j = i + 1; j < l; ++j) {
                bool similar = isSimilar(A[i], A[j]);
                if (isSimilar(A[i], A[j])) {
                    // cout << A[i] << " " << A[j] << " " << similar << endl;
                    s.link(i, j);
                }
            }
        }
        return s.count();
    }
};

